import { LinearProgress, makeStyles, Typography } from "@material-ui/core";
import {React} from "react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ReactHtmlParser from "react-html-parser";
import CoinInfo from "../components/CoinInfo";
import { SingleCoin } from "../config/api";
import { numberWithCommas } from "../components/CoinsTable";
import { CryptoState } from "../CryptoContext";
import "./button.css";
import {Accordion, InputGroup, FormControl, Button} from 'react-bootstrap';


const CoinPage = () => {
  const { id } = useParams();
  const [coin, setCoin] = useState();

  const { currency, symbol } = CryptoState();
  // const [lgShow, setLgShow] = useState(false);



  const fetchCoin = async () => {
    const { data } = await axios.get(SingleCoin(id));

    setCoin(data);  

  };

  useEffect(() => {
    fetchCoin();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);


  const useStyles = makeStyles((theme) => ({
    container: {
      display: "flex",
      [theme.breakpoints.down("md")]: {
        flexDirection: "column",
        alignItems: "center",
      },
    },
    sidebar: {
      width: "30%",
      [theme.breakpoints.down("md")]: {
        width: "100%",
      },
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      marginTop: 25,
      borderRight: "2px solid grey",
    },
    heading: {
      fontWeight: "bold",
      marginBottom: 20,
      fontFamily: "Montserrat",
    },
    description: {
      width: "100%",
      fontFamily: "Montserrat",
      padding: 25,
      paddingBottom: 15,
      paddingTop: 0,
      textAlign: "justify",
    },
    marketData: {
      alignSelf: "start",
      padding: 25,
      paddingTop: 10,
      width: "100%",
      [theme.breakpoints.down("md")]: {
        display: "flex",
        justifyContent: "space-around",
      },
      [theme.breakpoints.down("sm")]: {
        flexDirection: "column",
        alignItems: "center",
      },
      [theme.breakpoints.down("xs")]: {
        alignItems: "start",
      },
    },
  }));


  const classes = useStyles();

  if (!coin) return <LinearProgress style={{ backgroundColor: "gold" }} />;

  return (
    <div className={classes.container}>
      <div className={classes.sidebar}>
        <img
          src={coin?.image.large}
          alt={coin?.name}
          height="200"
          style={{ marginBottom: 20 }}
        />
        <Typography variant="h3" className={classes.heading}>
          {coin?.name}
        </Typography>
        <Typography variant="subtitle1" className={classes.description}>
          {ReactHtmlParser(coin?.description.en.split(". ")[0])}.
        </Typography>
        <div className={classes.marketData}>
          <span style={{ display: "flex" }}>
            <Typography variant="h5" className={classes.heading}>
              Rank:
            </Typography>
            &nbsp; &nbsp;
            <Typography
              variant="h5"
              style={{
                fontFamily: "Montserrat",
              }}
            >
              {numberWithCommas(coin?.market_cap_rank)}
            </Typography>
          </span>

          <span style={{ display: "flex" }}>
            <Typography variant="h5" className={classes.heading}>
              Current Price:
            </Typography>
            &nbsp; &nbsp;
            <Typography
              variant="h5"
              style={{
                fontFamily: "Montserrat",
              }}
            >
              {symbol}{" "}
              {numberWithCommas(
                coin?.market_data.current_price[currency.toLowerCase()]
              )}
            </Typography>
          </span>
          <span style={{ display: "flex" }}>
            <Typography variant="h5" className={classes.heading}>
              Market Cap:
            </Typography>
            &nbsp; &nbsp;
            <Typography
              variant="h5"
              style={{
                fontFamily: "Montserrat",
              }}
            >
              {symbol}{" "}
              {numberWithCommas(
                coin?.market_data.market_cap[currency.toLowerCase()]
                  .toString()
                  .slice(0, -6)
              )}
            </Typography>
          </span>


          <Accordion>
            <Accordion.Item eventKey="0">
              <Accordion.Header>BUY Coins!</Accordion.Header>
              <Accordion.Body>
                <InputGroup className="mb-3">
                  <InputGroup.Text type="text" id="inputGroup-sizing-default" > At Price  <b>INR</b> </InputGroup.Text> <br />
                  <FormControl
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>

                <InputGroup className="mb-3">
                <InputGroup.Text id="inputGroup-sizing-default"> Amount  <b>Data</b> </InputGroup.Text> <br />
                  <FormControl
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>

                <InputGroup className="mb-3">
                <InputGroup.Text id="inputGroup-sizing-default"> Total <b>Amount</b> </InputGroup.Text> <br />
                  <FormControl
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>
                <Button variant="warning" size="sm">
                  Buy Data
                </Button>{' '}
              </Accordion.Body>
            </Accordion.Item>
            <Accordion.Item eventKey="1">
              <Accordion.Header>SELL Coins!</Accordion.Header>
              <Accordion.Body>
              <InputGroup className="mb-3">
                  <InputGroup.Text id="inputGroup-sizing-default"> At Price  <b>INR</b> </InputGroup.Text> <br />
                  <FormControl
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>

                <InputGroup className="mb-3">
                <InputGroup.Text id="inputGroup-sizing-default"> Amount  <b>Data</b> </InputGroup.Text> <br />
                  <FormControl
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>

                <InputGroup className="mb-3">
                <InputGroup.Text id="inputGroup-sizing-default"> Total <b>Amount</b> </InputGroup.Text> <br />
                  <FormControl
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>
                <Button variant="warning" size="sm">
                  Sell Data
                </Button>{' '}
              </Accordion.Body>
            </Accordion.Item>
          </Accordion>


        </div>
      </div>
        <CoinInfo coin={coin} />
    </div>
  );

};

export default CoinPage;
